    public void setaVisualizacao(){
        campoPlaca.setText(veiculoAux.getPlaca());
        campoModelo.setText(veiculoAux.getModelo());
        campoMarca.setText(veiculoAux.getMarca());
        comboBoxPais.setSelectedItem(veiculoAux.getPais());
        campoAno.setText(veiculoAux.getAno());
        campoRenavam.setText(veiculoAux.getRenavam());
        camponEixos.setText(veiculoAux.getNumeroEixos());
        campoChassi.setText(veiculoAux.getChassi());
        campoNMotor.setText(veiculoAux.getNroMotor());
        campoCor.setText(veiculoAux.getCor());
        
        Iterator<String> iteradorCaract = veiculoAux.getListaCaracteristicas().iterator();
        String caractAux;
        int index = 1;
        while(iteradorCaract.hasNext()){
            caractAux = iteradorCaract.next();
            DefaultTableModel modeloAux = (DefaultTableModel) tabelaCaract1.getModel();
            modeloAux.addRow(new Object[]{index, caractAux});
            index++;
        }
        
    }
    
    private void editaVeiculo(){
        getVeiculoController().fechaVisualizacaoVeiculoParaEdicao();
    
    }
    
    private void fechaVisualizacao(){
        getVeiculoController().fechaVisualizacaoVeiculo();
    }
    
    
